import React, { Component } from 'react'
import logo from './logo.svg';
import './App.css';
import API from './adapters/API'
import Model from './components/Model'

export default class App extends Component {

  state = {
    allAbilities: [],
    allFactionKeywords: [],
    allKeywords: [],
    allModels: [],
    allUnits: [],
    allUsers: [],
    allWargearOptions: [],
    allWeapons: []

  }

  componentDidMount() {
    API.getAllModels()
      .then(allModels => this.setState({ allModels }))
  }


  render() {

    const models = this.state.allModels.map(model => ({ ...model}))
    return (
      <div className="App">
      {
            models.map(model => <Model key={model.id} {...model} clickHandler={() => this.props.modelClickHandler(model)} />)
      }
    </div>
    )
  }
}
